// src/components/RegisterWorker.js
import React, { useState } from 'react';
import { TextField, Button, Container, Typography, Alert } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const RegisterWorker = () => {
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    password: '',
    skills: '',
    qualifications: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.email || !formData.password || !formData.skills) {
      setError('Please fill in all required fields.');
      return;
    }

    try {
      const worker = { ...formData, role: 'worker', attempts: 0 };
      await axios.post('http://localhost:5000/users', worker);
      await axios.post('http://localhost:5000/workers', {
        email: formData.email,
        skills: formData.skills.split(',').map(s => s.trim()),
        qualifications: formData.qualifications.split(',').map(q => q.trim()),
      });
      setSuccess('Worker account created successfully! Please log in.');
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setError('Error creating account. Please try again.');
    }
  };

  return (
    <Container maxWidth="sm" style={{ marginTop: '2rem' }}>
      <Typography variant="h4" gutterBottom>
        Register as Worker
      </Typography>
      {error && <Alert severity="error">{error}</Alert>}
      {success && <Alert severity="success">{success}</Alert>}
      <form onSubmit={handleSubmit}>
        <TextField
          label="Email"
          name="email"
          fullWidth
          margin="normal"
          value={formData.email}
          onChange={handleChange}
        />
        <TextField
          label="Username"
          name="username"
          fullWidth
          margin="normal"
          value={formData.username}
          onChange={handleChange}
        />
        <TextField
          label="Password"
          name="password"
          type="password"
          fullWidth
          margin="normal"
          value={formData.password}
          onChange={handleChange}
        />
        <TextField
          label="Skills (comma-separated)"
          name="skills"
          fullWidth
          margin="normal"
          value={formData.skills}
          onChange={handleChange}
        />
        <TextField
          label="Qualifications (comma-separated)"
          name="qualifications"
          fullWidth
          margin="normal"
          value={formData.qualifications}
          onChange={handleChange}
        />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Register
        </Button>
      </form>
    </Container>
  );
};

export default RegisterWorker;
